import React, {useEffect, useState} from 'react';
import './App.css';

const apiUrl = 'https://api.giphy.com/v1/gifs/search?api_key=pNzCkIiTkFG7uHzL5Om8ozYlYSO67VYl&q=panda&limit=25&offset=0&rating=g&lang=en';

function App() {
  const [gifs, setGifs] = useState([]);

  // se ejecuta al renderizar
  // 2nd param, dependencias que tiene el efecto
  useEffect(function () {
    //setGifs(DIFFERENT_GIFS)
    console.log('efecto ejecutado')
    fetch(apiUrl)
      .then(res => res.json())
      .then(response => {
        const {data} = response
        const gifs = data.map(image => image.images.downsized_medium.url)
        setGifs(gifs)
      })
  }, []);
  /*const state = useState([]);
  const gifs = state[0];
  const setGifs = state[1];*/
  return (
    <div className="App">
      <section className="App-content">
        {
          gifs.map(singleGif => <h3>{singleGif}</h3>)
        }
      </section>
    </div>
  );
}

export default App;
