import React, {useEffect, useState} from 'react';
import './App.css';
import getGifs from './services/getGifs';


function App() {
  const [gifs, setGifs] = useState([]);

  // se ejecuta al renderizar
  // 2nd param, dependencias que tiene el efecto
  useEffect(function () {
    getGifs({keyword: 'morty'}).then(gifs => setGifs(gifs))
  }, []);
  /*const state = useState([]);
  const gifs = state[0];
  const setGifs = state[1];*/
  return (
    <div className="App">
      <section className="App-content">
        {
          gifs.map(singleGif => {
           return <div>
              <h4>{singleGif.title}</h4>
              <img alt={singleGif.title} 
              src={singleGif.url}></img>
            </div>
        })
        }
      </section>
    </div>
  );
}

export default App;
